INSERT INTO `wp_posts` VALUES(15, 1, '2011-10-29 08:50:29', '2011-10-29 08:50:29', 'We source pieces from all over the UK as well as countries such as Denmark, France and Germany. We find unusual, individual and distinctive pieces of furniture to create an interesting range of styles. <a>Why not visit and discover this month’s special pieces?</a>\r\n\r\nWe also provide a bespoke furniture making service for your special requirements.\r\n\r\n[eshop_list_subpages records="all"]', 'Furniture', '', 'publish', 'closed', 'closed', '', 'furniture', '', '', '2011-10-29 09:43:37', '2011-10-29 09:43:37', '', 0, 'http://localhost/sandrajane/?page_id=15', 20, 'page', '', 0);
